package core;

public class Swap_two_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 11;
		int b = 100;
		int c;
		
		c=a;
		a=b;  //100
		//11
		b=c; //11
		
		System.out.println("Swapping a number using third variable:" +a);
		System.out.println("Swapping b number using third variable:" +b);
		
		
	}

}
